import React, { useState } from 'react';
import axios from 'axios';

const AddUser = () => {
  const [form, setForm] = useState({ username: '', password: '', email: '', role: 'USER' });
  const [message, setMessage] = useState('');

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setMessage('');
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:9000/api/users', form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('Kullanıcı başarıyla eklendi!');
    } catch (err) {
      setMessage(err.response?.data || 'Hata oluştu!');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{maxWidth:400, margin:'24px auto', background:'#fff', padding:24, borderRadius:8}}>
      <h3>Yeni Kullanıcı Ekle</h3>
      <input name="username" placeholder="Kullanıcı Adı" value={form.username} onChange={handleChange} required />
      <input name="password" type="password" placeholder="Şifre" value={form.password} onChange={handleChange} required />
      <input name="email" type="email" placeholder="E-posta" value={form.email} onChange={handleChange} required />
      <select name="role" value={form.role} onChange={handleChange}>
        <option value="USER">USER</option>
        <option value="ADMIN">ADMIN</option>
      </select>
      <button type="submit">Ekle</button>
      {message && <div style={{marginTop:10, color: message.includes('başarı') ? 'green' : 'red'}}>{message}</div>}
    </form>
  );
};

export default AddUser;
