import React, { useState, useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ProjectList from './components/ProjectList';
import AddProject from './components/AddProject';
import EditProject from './components/EditProject';
import Login from './components/Login';
import AddUser from './components/AddUser';
import { ThemeProvider, ThemeContext } from './ThemeContext';
import './App.css';

function ThemeToggle() {
  const { theme, setTheme } = useContext(ThemeContext);
  return (
    <button onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')} style={{position:'fixed',top:10,right:10}}>
      {theme === 'light' ? 'üåô Koyu Mod' : '‚òÄÔ∏è A√ßƒ±k Mod'}
    </button>
  );
}

function AppContent() {
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('token'));
  const token = localStorage.getItem('token');
  let isAdmin = false;
  if (token) {
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      isAdmin = payload.roles && payload.roles.includes('ROLE_ADMIN');
    } catch {}
  }

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    window.location.href = '/';
  };

  return (
    <Router>
      <ThemeToggle />
      <header className="navbar">
        <div className="container">
          <nav>
            <Link to="/">Projeler</Link>
            <Link to="/add">Proje Ekle</Link>
            {isAdmin && <Link to="/add-user">Kullanƒ±cƒ± Ekle</Link>}
            {!isLoggedIn && <Link to="/login">Giri≈ü Yap</Link>}
            {isLoggedIn && <button onClick={handleLogout}>√áƒ±kƒ±≈ü Yap</button>}
          </nav>
        </div>
      </header>
      <main className="container">
        <Routes>
          <Route path="/" element={<ProjectList />} />
          <Route path="/add" element={<AddProject />} />
          <Route path="/edit/:id" element={<EditProject />} />
          <Route path="/login" element={<Login onLogin={() => setIsLoggedIn(true)} />} />
          {isAdmin && <Route path="/add-user" element={<AddUser />} />}
        </Routes>
      </main>
    </Router>
  );
}

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

export default App;
